package view;import exception.IsException;import repository.CourseRepository;import service.CourseService;import java.util.Scanner;public class CourseView {    private final CourseRepository courseService = new CourseService();    public void menu() throws IsException {        while (true) {            System.out.println("=".repeat(50));            System.out.println("1. Add new Course");            System.out.println("2. List Course");            System.out.println("3. Find Course By ID");            System.out.println("4. Find Course By Title");            System.out.println("5. Remove Course By ID");            System.out.println("0. Exit.");            System.out.println("=".repeat(50));            System.out.print("[+] Insert option: ");            String option = new Scanner(System.in).nextLine();            switch (option) {                case "1" -> {                    courseService.createCourse();                }                case "2" -> {                    courseService.listCourse();                }                case "3" -> {                    courseService.searchById();                    break;                }                case "4" -> {                    courseService.searchByName();                }                case "5" -> {                    courseService.removeById();                }                case "0" -> {                    System.exit(0);                }                default -> System.out.println("Please, Input the right option [0->5]: ");            }        }    }}